import React from 'react';

function Tasks() {
  const categories = [
    { name: 'Works', tasks: 3 },
    { name: 'Sport', tasks: 10 },
    { name: 'Habits', tasks: 4 },
  ];

  const taskList = ['Email Check', 'Weekly Meeting', 'Team Discussion'];

  return (
    <div style={{ display: 'flex', justifyContent: 'center', padding: '20px' }}>
      <div style={{ width: '30%', marginRight: '20px' }}>
        {categories.map((category, index) => (
          <div
            key={index}
            style={{
              border: '1px solid #ddd',
              borderRadius: '8px',
              padding: '10px',
              marginBottom: '10px',
              textAlign: 'center',
            }}
          >
            <h3>{category.name}</h3>
            <p>+{category.tasks} tasks</p>
          </div>
        ))}
      </div>
      <div style={{ width: '40%' }}>
        <h3>Task List</h3>
        {taskList.map((task, index) => (
          <div key={index} style={{ marginBottom: '10px' }}>
            <input type="checkbox" id={`task-${index}`} />
            <label htmlFor={`task-${index}`} style={{ marginLeft: '10px' }}>
              {task}
            </label>
          </div>
        ))}
        <button
          style={{
            padding: '10px 20px',
            backgroundColor: 'black',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            marginTop: '10px',
          }}
        >
          End All Tasks
        </button>
      </div>
    </div>
  );
}

export default Tasks;
