import React from "react";
import WorkIcon from "../assets/Work.svg";
import HeartIcon from "../assets/Heart.svg";

function TaskList() {
  const categories = [
    { name: "Work", icon: WorkIcon, tasks: 3 },
    { name: "Habits", icon: HeartIcon, tasks: 4 },
  ];

  const tasks = ["Email Check", "Weekly Meeting", "Team Discussion"];

  return (
    <div style={{ padding: "20px", backgroundColor: "#f8f9fa", minHeight: "100vh" }}>
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "20px" }}>
        <h1>Task List</h1>
        <button style={{ border: "none", backgroundColor: "transparent", fontSize: "24px" }}>â˜°</button>
      </header>

      <div style={{ display: "flex", gap: "20px", marginBottom: "30px" }}>
        {categories.map((category, index) => (
          <div
            key={index}
            style={{
              backgroundColor: "#fff",
              padding: "20px",
              borderRadius: "12px",
              boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
              textAlign: "center",
              flex: "1",
            }}
          >
            <img src={category.icon} alt={`${category.name} Icon`} style={{ width: "40px", marginBottom: "10px" }} />
            <h4>{category.name}</h4>
            <p>{`+${category.tasks} tasks`}</p>
          </div>
        ))}
      </div>

      <div>
        {tasks.map((task, index) => (
          <div key={index} style={{ display: "flex", alignItems: "center", marginBottom: "10px" }}>
            <input type="checkbox" style={{ marginRight: "10px" }} />
            <p>{task}</p>
          </div>
        ))}
        <button style={{ backgroundColor: "#000", color: "#fff", padding: "10px 20px", borderRadius: "8px", marginTop: "20px" }}>End All Tasks</button>
      </div>
    </div>
  );
}

export default TaskList;
