import React from 'react';

const TaskList = () => {
  const tasks = [
    { name: 'Email Check', completed: true },
    { name: 'Weekly Meeting', completed: false },
    { name: 'Team Discussion', completed: false },
  ];

  return (
    <div style={{ padding: '10px 20px' }}>
      {tasks.map((task, idx) => (
        <div key={idx} style={{ display: 'flex', justifyContent: 'space-between', margin: '10px 0' }}>
          <span>{task.name}</span>
          <input type="checkbox" checked={task.completed} />
        </div>
      ))}
      <button style={{ marginTop: '20px', padding: '10px 20px' }}>End All Task</button>
    </div>
  );
};

export default TaskList;
