import React from 'react';

const Categories = () => {
  const categories = [
    { name: 'Works', count: 3 },
    { name: 'Sport', count: 10 },
    { name: 'Habits', count: 4 },
  ];

  return (
    <div style={{ display: 'flex', gap: '10px', padding: '10px 20px' }}>
      {categories.map((cat, idx) => (
        <div key={idx} style={{ padding: '10px', borderRadius: '10px', backgroundColor: '#f0f0f0' }}>
          <h4>{cat.name}</h4>
          <p>+{cat.count} task</p>
        </div>
      ))}
    </div>
  );
};

export default Categories;
