function TaskList() {
    const categories = [
      { name: "Works", color: "#FFE59E", tasks: 3 },
      { name: "Sport", color: "#B9FBC0", tasks: 10 },
      { name: "Habits", color: "#DAD7FE", tasks: 4 },
    ];

    const tasks = ["Email Check", "Weekly Meeting", "Team Discussion"];

    return (
      <div className="task-container">
        <div className="top-bar">
          <span>Home</span>
          <span>â˜°</span>
          <span>ðŸ‘¤</span>
        </div>
        <div className="tabs">
          <div className="tab active">Task List</div>
          <div className="tab">Complete</div>
        </div>
        <h3>Categories</h3>
        <div className="categories">
          {categories.map((cat, i) => (
            <div
              key={i}
              className="category-card"
              style={{ background: cat.color }}
            >
              <h4>{cat.name}</h4>
              <p>+{cat.tasks} tasks</p>
            </div>
          ))}
        </div>
        <h3>Task List</h3>
        <div className="task-list">
          {tasks.map((task, i) => (
            <div key={i} className="task-item">
              <input type="checkbox" />
              <span>{task}</span>
            </div>
          ))}
        </div>
        <button className="cta-button">End All Tasks</button>
      </div>
    );
  }
