import React from 'react';
import './styles.css';

function App() {
  const categories = [
    { name: 'Works', tasks: 3 },
    { name: 'Sport', tasks: 10 },
    { name: 'Habits', tasks: 4 },
  ];

  const tasks = [
    { name: 'Email Check', completed: true },
    { name: 'Weekly Meeting', completed: true },
    { name: 'Team Discussion', completed: false },
  ];

  return (
    <div className="app-container">
      <h1>TÃ¢ches</h1>
      <div className="category-container">
        {categories.map((category, index) => (
          <div key={index} className="category-card">
            <h3>{category.name}</h3>
            <p>+{category.tasks} task</p>
          </div>
        ))}
      </div>
      <div className="task-list">
        {tasks.map((task, index) => (
          <div key={index} className="task-item">
            <input type="checkbox" checked={task.completed} />
            <span>{task.name}</span>
          </div>
        ))}
        <button className="end-task-button">End All Task</button>
      </div>
    </div>
  );
}

export default App;
