import React from 'react';
import { Link } from 'react-router-dom';

function Home() {
  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Tâches</h1>
      <p>Planifiez votre journée en détails</p>
      <Link to="/tasks">
        <button style={{ padding: '10px 20px', fontSize: '16px' }}>Voir les Tâches</button>
      </Link>
    </div>
  );
}

export default Home;
