import React from "react";
import ArrowRight from "../assets/arrow-right.png";

function About() {
    return (
        <div
            style={{
                backgroundImage: "url('/image.png')",
                backgroundSize: "contain",
                backgroundRepeat: "no-repeat",
                backgroundPosition: "center",
                height: "100vh", // Garde une hauteur pleine page
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
            }}
        >
            <h1 style={{ fontSize: "32px", fontWeight: "bold", marginBottom: "10px" }}>Tâches</h1>
            <p style={{ fontSize: "16px", color: "#555", marginBottom: "30px" }}>Planifiez votre journée en détails</p>
            <button
                style={{
                    backgroundColor: "#000",
                    color: "#fff",
                    borderRadius: "50%",
                    width: "60px",
                    height: "60px",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    position: "absolute",
                    bottom: "30px",
                }}
            >
                <img src={ArrowRight} alt="Arrow Icon" style={{ width: "24px" }} />
            </button>
        </div>
    );
}

export default About;
