import React from "react";
import ArrowRight from "../assets/arrow-right.png"; // Assurez-vous que ce fichier existe dans src/assets/

function About() {
  return (
    <div
      style={{
        backgroundColor: "#fef6e4",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        position: "relative",
      }}
    >
      {/* Titre */}
      <h1 style={{ fontSize: "32px", fontWeight: "bold", marginBottom: "10px" }}>Tâches</h1>
      <p style={{ fontSize: "16px", color: "#555", marginBottom: "30px" }}>
        Planifiez votre journée en détails
      </p>

      {/* Image */}
      <img
        src="/image.png" // Utilisation du chemin public
        alt="Illustration"
        style={{
          width: "200px",
          height: "auto",
          marginBottom: "30px",
          display: "block",
          border: "2px solid blue", // Bordure visible pour vérifier
        }}
      />

      {/* Bouton */}
      <button
        style={{
          backgroundColor: "#000",
          color: "#fff",
          borderRadius: "50%",
          width: "60px",
          height: "60px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          position: "absolute",
          bottom: "30px",
        }}
      >
        <img
          src={ArrowRight} // Assurez-vous que cette image est dans src/assets
          alt="Arrow Icon"
          style={{ width: "24px" }}
        />
      </button>
    </div>
  );
}

export default About;
