import React from "react";
import ArrowRight from "../assets/arrow-right.png";
 // Import the illustration

function About() {
    return (
        <div
          style={{
            backgroundColor: "#fef6e4",
            height: "100vh",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            position: "relative",
          }}
        >
          <h1 style={{ fontSize: "32px", fontWeight: "bold", marginBottom: "10px" }}>Tâches</h1>
          <p style={{ fontSize: "16px", color: "#555", marginBottom: "30px" }}>Planifiez votre journée en détails</p>
          <img
  src="/image.png"
  alt="Illustration"
  style={{
    width: "200px",
    height: "auto", // Ajuste automatiquement la hauteur
    marginBottom: "30px",
    border: "2px solid blue", // Bordure visible pour débogage
    display: "block", // Assure que l'image n'est pas masquée
  }}
/>

          <button
            style={{
              backgroundColor: "#000",
              color: "#fff",
              borderRadius: "50%",
              width: "60px",
              height: "60px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              position: "absolute",
              bottom: "30px",
            }}
          >
            <img src={ArrowRight} alt="Arrow Icon" style={{ width: "24px" }} />
          </button>
        </div>
      );
    
}

export default About;
