import React from "react";

function About() {
  return (
    <div
      style={{
        backgroundColor: "#fef6e4",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center", // Aligne tout au centre
      }}
    >
      {/* Image */}
      <div
        style={{
          width: "300px",
          height: "300px",
          backgroundImage: "url('./assets/image.png')", // Assurez-vous que l'image est bien dans le dossier public
          backgroundSize: "contain",
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center",
          marginBottom: "20px",
        }}
      ></div>

      {/* Titre */}
      <h1 style={{ fontSize: "32px", fontWeight: "bold", marginBottom: "10px" }}>
        Tâches
      </h1>

      {/* Description */}
      <p style={{ fontSize: "16px", color: "#555", marginBottom: "30px" }}>
        Planifiez votre journée en détails
      </p>

      {/* Bouton */}
      <button
        style={{
          backgroundColor: "#000",
          color: "#fff",
          borderRadius: "25px",
          padding: "10px 20px",
          fontSize: "16px",
          border: "none",
        }}
      >
        Voir les Tâches
      </button>
    </div>
  );
}

export default About;
