import React, { useEffect, useState } from 'react';
import Header from './components/Header';
import CategoryList from './components/CategoryList';
import TaskList from './components/TaskList';
import AddTaskForm from './components/AddTaskForm';
import AddCategoryForm from './components/AddCategoryForm';
import Footer from './components/Footer';
import { getCategories, getTasks } from './api';
import './style.css';

const App = () => {
    const [categories, setCategories] = useState([]);
    const [tasks, setTasks] = useState([]);

    // Fonction pour rafraîchir les catégories
    const refreshCategories = async () => {
        const data = await getCategories();
        setCategories(data);
    };

    // Fonction pour rafraîchir les tâches
    const refreshTasks = async () => {
        const data = await getTasks();
        setTasks(data);
    };

    // Récupère les données au chargement
    useEffect(() => {
        refreshCategories();
        refreshTasks();
    }, []);

    return (
        <div className="container">
            {/* En-tête */}
            <Header />

            {/* Liste des catégories */}
            <CategoryList categories={categories} />

            {/* Liste des tâches */}
            <TaskList tasks={tasks} refreshTasks={refreshTasks} />

            {/* Formulaire pour ajouter des tâches */}
            <AddTaskForm refreshTasks={refreshTasks} />

            {/* Formulaire pour ajouter des catégories */}
            <AddCategoryForm refreshCategories={refreshCategories} />

            {/* Pied de page */}
            <Footer />
        </div>
    );
};

export default App;
